const stripe=Stripe(stripePublicKey);initialize();function getClientSecret(){return new Promise(function(resolve,reject){postAjax("CreateStripeClientSecret",null,function(result){console.log(result);resolve(result.clientSecret)})})}async function initialize(){const fetchClientSecret=async()=>{return getClientSecret()};const checkout=await stripe.initEmbeddedCheckout({fetchClientSecret:fetchClientSecret});checkout.mount("#stripeCheckout")}